export const scrollToTop = () => {
  const bannerElement = document.getElementById("banner");
  if (bannerElement) {
    bannerElement.scrollIntoView({ behavior: "smooth" });
  }
};

export const handleClickScroll = (id) => {const element = document.getElementById('meuElemento');

if (element) {
  element.style.scrollSnapType = 'y mandatory';
  element.style.scrollSnapAlign = 'start';
  element.style.overflow = 'scroll';
}
}
