import React, { useEffect, useState } from "react";
import SlickSlider from "../SlickSlider/SlickSlider";
import icon01 from "../../assets/img/icon/choose_icon01.svg";
import icon02 from "../../assets/img/icon/choose_icon02.svg";
import icon03 from "../../assets/img/icon/choose_icon03.svg";
import icon04 from "../../assets/img/icon/choose_icon04.svg";
import WhyChooseUsItem from "./WhyChooseUsItem";

const WhyChooseUs = () => {
  const slickSettings = {
    dots: false,
    infinite: true,
    speed: 1000,
    autoplay: true,
    arrows: false,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1200,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
        },
      },
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 767,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: false,
        },
      },
      {
        breakpoint: 575,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: false,
        },
      },
    ],
  };

  const slider_items = [
    {
      src: icon01,
      alt: "",
      link: "/",
      title: "Valor acessível",
      description: `Acreditamos que tecnologia de qualidade não precisa ser inacessível. 
      Oferecemos soluções de software com excelente custo-benefício, 
      proporcionando um retorno financeiro sólido para o seu investimento. 
      Nosso objetivo é fornecer resultados excepcionais sem comprometer o seu orçamento.`,
    },
    {
      src: icon02,
      alt: "",
      link: "/",
      title: "Qualidade em cada detalhe",
      description: `Nosso compromisso com a qualidade é fundamental. 
      Desenvolvemos softwares com alto padrão de qualidade, com ênfase na funcionalidade, 
      usabilidade e performance. Cada linha de código é cuidadosamente revisada e 
      testada para garantir um produto final de excelência.`,
    },
    {
      src: icon03,
      alt: "",
      link: "/",
      title: "Privacidade Garantida",
      description: `Respeitamos a privacidade dos nossos clientes. 
      Adotamos medidas rigorosas para proteger os dados confidenciais do seu negócio. 
      Todas as informações fornecidas são tratadas com o mais alto grau de confidencialidade e sigilo, 
      seguindo as melhores práticas de privacidade e conformidade.`,
    },
    {
      src: icon04,
      alt: "",
      link: "/",
      title: "Segurança em Primeiro Lugar",
      description: `Priorizamos a segurança dos seus dados e informações. 
      Nossos softwares são desenvolvidos com as mais avançadas práticas de segurança, 
      garantindo a proteção dos seus dados e proporcionando tranquilidade para o seu negócio.`,
    },    
    {
      src: icon01,
      alt: "",
      link: "/",
      title: "Valor acessível",
      description: `Acreditamos que tecnologia de qualidade não precisa ser inacessível. 
      Oferecemos soluções de software com excelente custo-benefício, 
      proporcionando um retorno financeiro sólido para o seu investimento. 
      Nosso objetivo é fornecer resultados excepcionais sem comprometer o seu orçamento.`,
    },
    {
      src: icon02,
      alt: "",
      link: "/",
      title: "Qualidade em cada detalhe",
      description: `Nosso compromisso com a qualidade é fundamental. 
      Desenvolvemos softwares com alto padrão de qualidade, com ênfase na funcionalidade, 
      usabilidade e performance. Cada linha de código é cuidadosamente revisada e 
      testada para garantir um produto final de excelência.`,
    },
    {
      src: icon03,
      alt: "",
      link: "/",
      title: "Privacidade Garantida",
      description: `Respeitamos a privacidade dos nossos clientes. 
      Adotamos medidas rigorosas para proteger os dados confidenciais do seu negócio. 
      Todas as informações fornecidas são tratadas com o mais alto grau de confidencialidade e sigilo, 
      seguindo as melhores práticas de privacidade e conformidade.`,
    },
    {
      src: icon04,
      alt: "",
      link: "/",
      title: "Segurança em Primeiro Lugar",
      description: `Priorizamos a segurança dos seus dados e informações. 
      Nossos softwares são desenvolvidos com as mais avançadas práticas de segurança, 
      garantindo a proteção dos seus dados e proporcionando tranquilidade para o seu negócio.`,
    },           
  ];

  const initalState = 0;
  const [count, setCount] = useState(initalState);

  useEffect(() => {
    const interval = setInterval(() => {
      setCount((old) => (old == 6 ? 0 : old + 1));
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const toMultiply = 100 / slider_items.length;

  return (
    <section className="choose-area pb-130">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-xl-6">
            <div className="section-title text-center mb-50">
              <span className="sub-title">Por que nos escolher? </span>
              <h2 className="title">
                Por que somos os melhores para o seu <span>negócio?</span>
              </h2>
            </div>
          </div>
        </div>

        <div className="row choose-active">
          <SlickSlider settings={slickSettings}>
            {slider_items.map((item, index) => (
              <div key={index} className="col-lg-3">
                <WhyChooseUsItem item={item} />
              </div>
            ))}
          </SlickSlider>
        </div>

        <div className="slide-progressbar">
          <div
            style={{
              // width: `${count * toMultiply}%`,
              backgroundSize: `${count * toMultiply}% 100%`,
            }}
            className="slide-filler"
          />
        </div>

        {/* <div
          className="slide-progress"
          role="progressbar"
          aria-valuemin="0"
          aria-valuemax="100"
        >
          <span className="slider__label sr-only"></span>
        </div> */}
      </div>
    </section>
  );
};

export default WhyChooseUs;
